// Initalize
Wrk_BufferUB := UPPER_BOUND(Ext_Buffer, 1);
Wrk_BufferLB := LOWER_BOUND(Ext_Buffer, 1);
Wrk_ExtBufferSize := Wrk_BufferUB - Wrk_BufferLB + 1;
Wrk_Size :=0;

// Type: Null
Wrk_Buffer[0] := 16#05;

// Copy object to external buffer
Wrk_Size := 2;		// Number of bytes to copy
IF Wrk_Size <= Wrk_ExtBufferSize THEN	// External buffer has space
	MemCopy(In:=Wrk_Buffer[0], AryOut:=Ext_Buffer[Wrk_BufferLB], Size:=Wrk_Size);
ELSE
	Wrk_Size := 0;
END_IF;

HE_SNMP_EncodeNull := Wrk_Size;	// Return number of bytes added to external array
