Inp_StateData.Sts_Visible:=TRUE;

IF Inp_StateData.Sts_State THEN
	
	IF Inp_StateData.Cfg_Alarm THEN
		Sts_AlarmSuffix:=	Inp_StateData.Sts_Desc;
		Sts_AlarmState:=	TRUE;
	END_IF;
	
	CASE Inp_StateData.Cfg_Action OF
		
		0://Use value
		IF Sts_ValueState = 0 THEN
			Sts_ValueState:=0;
		END_IF;
		
		1://Hold value
		IF Sts_ValueState < 2 THEN
			Sts_ValueState:=1;
		END_IF;
		
		2://Pre defined value
		Sts_ValueState:=2;
		
	END_CASE;
	
	CASE Inp_StateData.Cfg_Quality OF
		
		0://Good
		IF Sts_Quality = 0 THEN
			Sts_Quality := 0;
		END_IF;
		
		1://Uncetian
		IF Sts_Quality < 2 THEN
			Sts_Quality:=1;
		END_IF;
		
		2://Bad
		Sts_Quality:=2;
	END_CASE;
END_IF;
